import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { addToCart } from "../actions/cartAction";
import { Row, Col, ListGroup, Image, Button,Form } from "react-bootstrap";
import Message from "../components/Message";
import { Link } from "react-router-dom";
import { removeFromCart } from "../actions/cartAction";
const CartScreen = ({ match, location, history }) => {
  const productId = match.params.id;
  const qty = location.search ? Number(location.search.split("=")[1]) : [];

  const dispatch = useDispatch();

  const cart = useSelector((state) => state.cart);
  const { cartItems } = cart;

  useEffect(() => {
    if (productId) {
        console.log('this also calls')

      dispatch(addToCart(productId, qty));
    }
  }, [dispatch,productId,qty]);

  const removeFromCartHandler=(id)=>{
      console.log('this calls',id);
      dispatch(removeFromCart(id));
  }

  const checkOutHandler=()=>{
    history.push('/login?redirect=shipping')
  }
  return (
    <Row>
      <Col md={8}>
        <h1>SHOPPING CART</h1>
        {cartItems.length === 0 ? (
          <Message>
            Your Cart is empty <Link to="/">Go Back</Link>
          </Message>
        ) : (
          <ListGroup variant="flush">
            {cartItems.map((item) => (
              <ListGroup.Item key={item.product}>
                <Row>
                  <Col md={2}>
                    <Image
                      src={item.image}
                      alt={item.name}
                      fluid
                      rounded
                    ></Image>
                  </Col>
                  <Col md={3}>
                    <Link to={`/product/${item.product}`}>{item.name}</Link>
                  </Col>
                  <Col md={2}>{item.price}&#8377;</Col>
                  <Col md={2}>
                    <Form.Control
                      as="select"
                      value={item.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(item.product, Number(e.target.value))
                        )
                      }
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </Form.Control>
                  </Col>
                  <Col md={2}>
                    <Button
                      type="button"
                      variant="light"
                      onClick={()=>removeFromCartHandler(item.product)}
                    >   
                      <i className="fas fa-trash"></i>
                    </Button>
                  </Col>
                </Row>
              </ListGroup.Item>
            ))}
          </ListGroup>
        )}
      </Col>
      <Col md={4}>
        <ListGroup variant='flush'>
            <ListGroup.Item>
                <h2>SubTotal ({cartItems.reduce((acc,item)=> acc+item.qty,0)}) items</h2>
                {cartItems.reduce((acc,item)=> acc+item.qty * item.price,0).toFixed(2)}&#8377;
            </ListGroup.Item>
            <ListGroup.Item>
                <Button type="button" className='btn-block' disabled={cartItems.length===0} onClick={checkOutHandler}>Proceed To Checkout</Button>
            </ListGroup.Item>
        </ListGroup>
      </Col>
    </Row>
  );
};

export default CartScreen;
