import axios from "axios"
import { PRODUCT_LIST_FAILURE,PRODUCT_LIST_SUCCESS,PRODUCT_LIST_REQUEST } from "../constants/productConstants"


export const listProduct=()=>async(dispatch)=>{
    try{
        dispatch({type:PRODUCT_LIST_REQUEST})

        const {data}=await axios.get('/api/roduct');

        dispatch({type:PRODUCT_LIST_SUCCESS,payload:data})
    }catch(e){
        dispatch({type:PRODUCT_LIST_FAILURE,payload:e.response && e.response.data.message ? e.response.data.message : e.message})
    }
}
